package com.sk.admin.web.view;import java.util.List;import javax.faces.bean.ManagedBean;import javax.faces.bean.ManagedProperty;import javax.faces.bean.ViewScoped;import com.sk.domain.Category;import com.sk.service.CategoryService;@ViewScoped@ManagedBeanpublic class CategoriesView extends BaseView {	private static final long serialVersionUID = -7590244051767457979L;	@ManagedProperty("#{categoryService}")	private transient CategoryService categoryService;	public void setCategoryService(CategoryService categoryService) {		this.categoryService = categoryService;	}	public List<Category> getAll() {		return categoryService.getAll();	}	public void deleteCategory(Category category) {		categoryService.delete(category);		facesHelper.addInfoMessage("category " + category.getName() + " is deleted");	}}