package com.sk.admin.web.view;import java.io.UnsupportedEncodingException;import javax.annotation.PostConstruct;import javax.faces.bean.ManagedBean;import javax.faces.bean.ManagedProperty;import javax.faces.bean.ViewScoped;import com.sk.domain.Category;import com.sk.service.CategoryService;@ViewScoped@ManagedBeanpublic class CategoryFormView extends BaseView {	private static final long serialVersionUID = -2165907826552573988L;	private Category category;	@ManagedProperty("#{categoryService}")	private transient CategoryService categoryService;	@PostConstruct	public void init() {		category = (Category) facesHelper.flashGet("category");		if (category == null) {			category = new Category();		} else {			facesHelper.flashClear("category");		}	}	public void createURIFromName() throws UnsupportedEncodingException {		categoryService.createURLFromName(getCategory());	}	public Category getCategory() {		return category;	}	public void setCategory(Category category) {		this.category = category;	}	public void setCategoryService(CategoryService categoryService) {		this.categoryService = categoryService;	}	private void saveProduct() {		category = categoryService.save(category);		facesHelper.addInfoMessage("category saved");	}	public String save() {		saveProduct();		return facesHelper.redirectFaces("categories");	}	public String apply() {		saveProduct();		return null;	}}