package com.sk.admin.web.view;import org.junit.Before;import org.junit.Test;import org.junit.runner.RunWith;import org.mockito.Mock;import org.mockito.runners.MockitoJUnitRunner;import com.sk.util.builder.ProductBuilder;import static org.mockito.Mockito.when;import static org.hamcrest.MatcherAssert.assertThat;import static org.hamcrest.Matchers.equalTo;import static org.hamcrest.Matchers.nullValue;@RunWith(MockitoJUnitRunner.class)public class ProductFormViewTest {	private ProductFormView productFormView;	@Mock	private FacesHelper facesHelper;	@Before	public void init() {		productFormView = new ProductFormView();		productFormView.setFacesHelper(facesHelper);	}	@Test	public void shouldRetrieveFromFlashForEdit() {		when(facesHelper.flashGet("product")).thenReturn(new ProductBuilder().id(-1l).build());				productFormView.init();		assertThat(productFormView.getProduct().getId(), equalTo(-1l));	}	@Test	public void shouldCreateANewProductForCreate() {		when(facesHelper.flashGet("product")).thenReturn(null);		productFormView.init();		assertThat(productFormView.getProduct().getId(), nullValue());	}}