package com.sk.domain;import javax.persistence.Basic;import javax.persistence.Entity;import javax.persistence.Table;@Entity@Table(name = "category")public class Category extends BaseEntity{	private static final long serialVersionUID = 8491496935318382561L;	@Basic	private String name;	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;			}}