package com.sk.domain;import javax.persistence.CascadeType;import javax.persistence.Entity;import javax.persistence.JoinColumn;import javax.persistence.OneToOne;import javax.persistence.Table;@Entity@Table(name="credit_card_payment_method")public class CreditCardPaymentMethod extends PaymentMethod {	private static final long serialVersionUID = 5855690590187104905L;	@OneToOne(cascade = CascadeType.ALL,orphanRemoval = false)	@JoinColumn(name = "credit_card_id")	private CreditCard creditCard;	public CreditCard getCreditCard() {		return creditCard;	}	public void setCreditCard(CreditCard creditCard) {		this.creditCard = creditCard;	}	}