package com.sk.domain;import javax.persistence.CascadeType;import javax.persistence.Entity;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.OneToOne;import javax.persistence.Table;@Entity@Table(name = "credit_card_payment_method")public class CreditCardPaymentMethod extends PaymentMethod {	private static final long serialVersionUID = 5855690590187104905L;	@OneToOne(cascade = CascadeType.ALL,orphanRemoval = false)	@JoinColumn(name = "credit_card_id")	private CreditCard creditCard;		@ManyToOne	private InstallmentPlan installmentPlan;		private Double bonusUsed = 0d;	public InstallmentPlan getInstallmentPlan() {		return installmentPlan;	}	public void setInstallmentPlan(InstallmentPlan installmentPlan) {		this.installmentPlan = installmentPlan;	}	public CreditCard getCreditCard() {		return creditCard;	}	public void setCreditCard(CreditCard creditCard) {		this.creditCard = creditCard;	}	public Double getBonusUsed() {		return bonusUsed;	}	public void setBonusUsed(Double bonusUsed) {		this.bonusUsed = bonusUsed;	}	public boolean isBonusRequested() {		return bonusUsed != null && bonusUsed != 0;	}	}
