package com.sk.domain;import java.util.Date;import javax.persistence.CascadeType;import javax.persistence.Entity;import javax.persistence.EnumType;import javax.persistence.Enumerated;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.OneToOne;import javax.persistence.Table;import javax.persistence.Temporal;import javax.persistence.TemporalType;@Entity@Table(name = "user_order")public class Order extends BaseEntity {	private static final long serialVersionUID = -1070749922133859098L;	@OneToOne(cascade = CascadeType.ALL, orphanRemoval = true)	@JoinColumn(name = "shopping_cart_id")	private ShoppingCart shoppingCart;	@Temporal(TemporalType.TIMESTAMP)	private Date orderDate = new Date();	@OneToOne(cascade = CascadeType.ALL, orphanRemoval = true)	@JoinColumn(name = "payment_method_id")	private PaymentMethod paymentMethod;	@ManyToOne	@JoinColumn(name = "shopper_id")	private Shopper shopper;	@Enumerated(EnumType.STRING)	private OrderStatus orderStatus;	public boolean isUnderReview() {		return OrderStatus.UNDERREVIEW.equals(orderStatus);	}		public boolean isRejectRequested() {		return OrderStatus.REJECT_REQUESTED.equals(orderStatus);	}	public ShoppingCart getShoppingCart() {		return shoppingCart;	}	public void setShoppingCart(ShoppingCart shoppingCart) {		this.shoppingCart = shoppingCart;	}	public Date getOrderDate() {		return orderDate;	}	public void setOrderDate(Date orderDate) {		this.orderDate = orderDate;	}	public PaymentMethod getPaymentMethod() {		return paymentMethod;	}	public void setPaymentMethod(PaymentMethod paymentMethod) {		this.paymentMethod = paymentMethod;	}	public OrderStatus getOrderStatus() {		return orderStatus;	}	public void setOrderStatus(OrderStatus orderStatus) {		this.orderStatus = orderStatus;	}	public Shopper getShopper() {		return shopper;	}	public void setShopper(Shopper shopper) {		this.shopper = shopper;	}}