package com.sk.domain;import java.util.ArrayList;import java.util.List;import javax.persistence.Basic;import javax.persistence.ElementCollection;import javax.persistence.Entity;import javax.persistence.ManyToOne;import javax.persistence.Table;@Entity@Table(name="product")public class Product extends BaseEntity {	private static final long serialVersionUID = -3368361716829981915L;	@ManyToOne	private Category category;	@Basic	private String title;	@Basic	private String description;	@Basic	private double price;	@ElementCollection	private List<Photo> photos = new ArrayList<Photo>();	public Category getCategory() {		return category;	}	public void setCategory(Category category) {		this.category = category;	}	public String getTitle() {		return title;	}	public void setTitle(String title) {		this.title = title;	}	public String getDescription() {		return description;	}	public void setDescription(String description) {		this.description = description;	}	public double getPrice() {		return price;	}	public void setPrice(double price) {		this.price = price;	}	public List<Photo> getPhotos() {		return photos;	}	public void setPhotos(List<Photo> photos) {		this.photos = photos;	}	}