package com.sk.domain;import java.util.ArrayList;import java.util.List;import javax.persistence.Basic;import javax.persistence.Column;import javax.persistence.ElementCollection;import javax.persistence.Entity;import javax.persistence.JoinTable;import javax.persistence.ManyToOne;import javax.persistence.Table;import com.sk.domain.coupon.CouponHolder;@Entity@Table(name="product")public class Product extends BaseEntity implements CouponHolder{	private static final long serialVersionUID = -3368361716829981915L;	@ManyToOne	private Category category;	@Basic	@Column(length=150)	private String title;		@Basic	@Column(length=500)	private String url;	@Basic	@Column(length=1000)	private String description;	@Basic	private double price;	@ElementCollection	@JoinTable( name = "product_photos")	private List<Photo> photos = new ArrayList<Photo>();	@Basic	private boolean featured;	public Category getCategory() {		return category;	}	public void setCategory(Category category) {		this.category = category;	}	public String getTitle() {		return title;	}	public void setTitle(String title) {		this.title = title;	}	public String getDescription() {		return description;	}	public void setDescription(String description) {		this.description = description;	}	public double getPrice() {		return price;	}	public void setPrice(double price) {		this.price = price;	}	public List<Photo> getPhotos() {		return photos;	}	public void setPhotos(List<Photo> photos) {		this.photos = photos;	}		public void addPhoto(Photo photo){		getPhotos().add(photo);	}	public void deletePhoto(Photo photoToDelete){		getPhotos().remove(photoToDelete);	}	public String getUrl() {		return url;	}	public void setUrl(String url) {		this.url = url;	}	public void setFeatured(boolean featured) {		this.featured = featured;			}	public boolean isFeatured() {		return featured;	}	@Override	public String getName() {		return title;	}}