package com.sk.domain.dao;import java.util.List;import org.springframework.stereotype.Repository;import com.sk.domain.Order;import com.sk.domain.Shopper;@Repositorypublic class OrderDao extends GenericDao<Order> {	public OrderDao() {		super(Order.class);	}	@SuppressWarnings("unchecked")	public List<Order> findByShopper(Shopper shopper) {		return getSession()				.createQuery("from Order where shopper=:shopper")				.setParameter("shopper", shopper)				.list();	}}