package com.sk.service;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.sk.domain.Order;import com.sk.domain.PaymentMethod;import com.sk.domain.ShoppingCart;import com.sk.domain.dao.OrderDao;@Servicepublic class OrderService {	@Autowired	private OrderDao orderDao;	@Transactional	public Order createOrder(ShoppingCart shoppingCart, PaymentMethod paymentMethod) {		Order order = new Order();		order.setPaymentMethod(paymentMethod);		order.setShoppingCart(shoppingCart);		return orderDao.persist(order);	}}