package com.sk.service;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.sk.domain.Product;import com.sk.domain.dao.ProductDao;@Servicepublic class ProductService {	@Autowired	private ProductDao productDao;	public void setProductDao(ProductDao productDao) {		this.productDao = productDao;	}	public Product save(Product product) {		return productDao.persist(product);	}}