package com.sk.service.payment;public enum ResponseStatus {	SUCCESS("Success",true),	NOT_VALID_RESPONSE("The Bank has returned an invalid response",false),	CONNECTION_PROBLEM("There is a problem while connecting to the server",false),	FAILURE("Your request is declined by the bank",false);	private String infoText;	private boolean successful;	private ResponseStatus(String infoText, boolean successful) {		this.infoText = infoText;		this.successful = successful;	}	public String getInfoText() {		return infoText;	}	public boolean isSuccessful() {		return successful;	}}