package com.sk.service.payment;public class VPOSResponse {	private String infoText;	private String detailMessage;	private ResponseStatus status;	public VPOSResponse(String infoText, String detailMessage, ResponseStatus status) {		this.infoText = infoText;		this.status = status;		this.detailMessage = detailMessage;	}	public VPOSResponse(ResponseStatus status) {		this.status = status;		this.infoText = status.getInfoText();		this.detailMessage = status.getDetailText();	}	public String getInfoText() {		return infoText;	}	public ResponseStatus getStatus() {		return status;	}		public boolean isSuccessful(){		return status.isSuccessful();	}	public String getDetailMessage() {		return detailMessage;	}	@Override	public String toString() {		return "VPOSResponse [infoText=" + infoText + ", detailMessage=" + detailMessage + ", status=" + status + "]";	}	}