package com.sk.service.payment;public class VPOSResponse {	private String infoText;	private ResponseStatus status;	public VPOSResponse(String infoText, ResponseStatus status) {		this.infoText = infoText;		this.status = status;	}	public VPOSResponse(ResponseStatus status) {		this.status = status;		this.infoText = status.getInfoText();	}	public String getInfoText() {		return infoText;	}	public ResponseStatus getStatus() {		return status;	}		public boolean isSuccessful(){		return status.isSuccessful();	}	}