package com.sk.service.payment.garanti;import org.apache.commons.lang.StringUtils;import org.xml.sax.Attributes;import org.xml.sax.SAXException;import org.xml.sax.helpers.DefaultHandler;// TODO: use http://www.xmlpull.org/ instead of SAX.public class GarantiResponseHandler extends DefaultHandler {	private String code;	private String reasonCode;	private String message;	private String errorMsg;	private String systemErrorMsg;	private String responseTag;	@Override	public void startElement(String uri, String localName, String qName, Attributes attributes) throws SAXException {		this.responseTag = qName;	}	@Override	public void characters(char[] ch, int start, int length) throws SAXException {		if (StringUtils.equalsIgnoreCase("errorMsg", responseTag)) {			errorMsg = new String(ch, start, length);		} else if (StringUtils.equalsIgnoreCase("message", responseTag)) {			message = new String(ch, start, length);		} else if (StringUtils.equalsIgnoreCase("reasonCode", responseTag)) {			reasonCode = new String(ch, start, length);		} else if (StringUtils.equalsIgnoreCase("code", responseTag)) {			code = new String(ch, start, length);		} else if (StringUtils.equalsIgnoreCase("SysErrMsg", responseTag)) {			systemErrorMsg = new String(ch, start, length);		}	}	public boolean hasError() {		return StringUtils.isNotBlank(getErrorMsg());	}	public String getCode() {		return code;	}	public void setCode(String code) {		this.code = code;	}	public String getReasonCode() {		return reasonCode;	}	public void setReasonCode(String reasonCode) {		this.reasonCode = reasonCode;	}	public String getMessage() {		return message;	}	public void setMessage(String message) {		this.message = message;	}	public String getErrorMsg() {		return errorMsg;	}	public void setErrorMsg(String errorMsg) {		this.errorMsg = errorMsg;	}	public String getSystemErrorMsg() {		return systemErrorMsg;	}	public void setSystemErrorMsg(String systemErrorMsg) {		this.systemErrorMsg = systemErrorMsg;	}}