package com.sk.service.util;import java.security.MessageDigest;import java.security.NoSuchAlgorithmException;import org.springframework.stereotype.Component;@Componentpublic class HashHelper {	public String hash(String plainText) {		try {			MessageDigest md = MessageDigest.getInstance("SHA1");			md.reset();			byte[] buffer = plainText.getBytes();			md.update(buffer);			byte[] digest = md.digest();			String hashData = "";			for (int i = 0; i < digest.length; i++) {				hashData += Integer.toString((digest[i] & 0xff) + 0x100, 16).substring(1);			}			return hashData.toUpperCase();		} catch (NoSuchAlgorithmException e) {			return null;		}	}}