package com.sk.service;import org.junit.Before;import org.junit.Test;import org.junit.runner.RunWith;import org.mockito.runners.MockitoJUnitRunner;import com.sk.domain.Order;import com.sk.domain.PaymentMethod;import com.sk.domain.ShoppingCart;import com.sk.domain.dao.OrderDao;import com.sk.util.builder.CreditCardPaymentMethodBuilder;import com.sk.util.builder.ShoppingCartBuilder;import static org.hamcrest.MatcherAssert.assertThat;import static org.hamcrest.Matchers.equalTo;@RunWith(MockitoJUnitRunner.class)public class OrderServiceTest {	private OrderService orderService;	@Before	public void init() {		orderService = new OrderService();		orderService.setOrderDao(new OrderDaoStub());	}	@Test	public void shouldCreateAndPersistOrder() {		ShoppingCart shoppingCart = new ShoppingCartBuilder().build();		PaymentMethod paymentMethod = new CreditCardPaymentMethodBuilder().build();		Order order = orderService.createOrder(shoppingCart, paymentMethod);		assertThat(order.getPaymentMethod(), equalTo(paymentMethod));		assertThat(order.getShoppingCart(), equalTo(shoppingCart));	}	private static class OrderDaoStub extends OrderDao {		@Override		public Order persist(Order toPersist) {			return toPersist;		}	}}