package com.sk.service;import java.util.ArrayList;import java.util.List;import org.junit.Before;import org.junit.Test;import org.junit.runner.RunWith;import org.mockito.Mock;import org.mockito.runners.MockitoJUnitRunner;import com.sk.domain.Product;import com.sk.domain.dao.ProductDao;import com.sk.util.builder.ProductBuilder;import static org.mockito.Mockito.verify;import static org.mockito.Mockito.when;import static org.hamcrest.MatcherAssert.assertThat;import static org.hamcrest.Matchers.equalTo;@RunWith(MockitoJUnitRunner.class)public class ProductServiceTest {	@Mock	private ProductDao productDao;	private ProductService productService;	@Before	public void init() {		productService = new ProductService();		productService.setProductDao(productDao);	}	@Test	public void shouldPersist() {		Product product = new ProductBuilder().build();		productService.save(product);		verify(productDao).persist(product);	}	@Test	public void shouldGetAllProductsFromDao() {		List<Product> products = new ArrayList<Product>();		products.add(new ProductBuilder().build());		when(productDao.getAll()).thenReturn(products);				List<Product> fromService = productService.getAll();				assertThat(fromService.size(),equalTo(1));			}}