package com.sk.util.builder;import org.apache.commons.lang.RandomStringUtils;import com.sk.domain.Category;public class CategoryBuilder extends BaseBuilder<Category, CategoryBuilder> {	private String name = RandomStringUtils.randomAlphabetic(10);	@Override	protected Category doBuild() {		Category category = new Category();		category.setName(name);		return category;	}	public CategoryBuilder name(String name) {		this.name = name;		return this;	}}