package com.sk.util.builder;import org.apache.commons.lang.RandomStringUtils;import com.sk.domain.Category;public class CategoryBuilder extends BaseBuilder<Category, CategoryBuilder> {	private String name = RandomStringUtils.randomAlphabetic(10);	private String url;	private String description = RandomStringUtils.randomAlphabetic(500);	@Override	protected Category doBuild() {		Category category = new Category();		category.setName(name);		category.setUrl(url);		category.setDescription(description);		return category;	}	public CategoryBuilder name(String name) {		this.name = name;		return this;	}	public CategoryBuilder url(String url) {		this.url = url;		return this;	}	public CategoryBuilder description(String description) {		this.description = description;		return this;	}}