package com.sk.util.builder;import org.apache.commons.lang.RandomStringUtils;import com.sk.domain.CreditCardPaymentMethod;public class CreditCardPaymentMethodBuilder extends BaseBuilder<CreditCardPaymentMethod, CreditCardPaymentMethodBuilder> {	private String creditCardNumber = RandomStringUtils.randomNumeric(16);	@Override	protected CreditCardPaymentMethod doBuild() {		CreditCardPaymentMethod cardPaymentMethod = new CreditCardPaymentMethod();		cardPaymentMethod.setCardNumber(creditCardNumber);		return cardPaymentMethod;	}	public CreditCardPaymentMethodBuilder creditCardNumber(String creditCardNumber) {		this.creditCardNumber = creditCardNumber;		return this;	}}