package com.sk.util.builder;import com.sk.domain.CreditCard;import com.sk.domain.CreditCardPaymentMethod;import com.sk.domain.InstallmentPlan;public class CreditCardPaymentMethodBuilder extends BaseBuilder<CreditCardPaymentMethod, CreditCardPaymentMethodBuilder> {	private CreditCard creditCard = new CreditCardBuilder().build();	private InstallmentPlan installmentPlan;	@Override	protected CreditCardPaymentMethod doBuild() {		CreditCardPaymentMethod cardPaymentMethod = new CreditCardPaymentMethod();		cardPaymentMethod.setCreditCard(creditCard);		cardPaymentMethod.setInstallmentPlan(installmentPlan);		return cardPaymentMethod;	}	public CreditCardPaymentMethodBuilder creditCard(CreditCard creditCard) {		this.creditCard = creditCard;		return this;	}	public CreditCardPaymentMethodBuilder installmentPlan(InstallmentPlan installmentPlan) {		this.installmentPlan = installmentPlan;		return this;	}}