package com.sk.util.builder;import com.sk.domain.CreditCard;import com.sk.domain.CreditCardPaymentMethod;public class CreditCardPaymentMethodBuilder extends BaseBuilder<CreditCardPaymentMethod, CreditCardPaymentMethodBuilder> {	private CreditCard creditCard = new CreditCardBuilder().build();	@Override	protected CreditCardPaymentMethod doBuild() {		CreditCardPaymentMethod cardPaymentMethod = new CreditCardPaymentMethod();		cardPaymentMethod.setCreditCard(creditCard);		return cardPaymentMethod;	}	public CreditCardPaymentMethodBuilder creditCard(CreditCard creditCard) {		this.creditCard = creditCard;		return this;	}}