package com.sk.util.builder;import java.util.Date;import com.sk.domain.Order;import com.sk.domain.PaymentMethod;import com.sk.domain.ShoppingCart;public class OrderBuilder extends BaseBuilder<Order, OrderBuilder> {	private Date orderDate;	private ShoppingCart shoppingCart;	private PaymentMethod paymentMethod = new CreditCardPaymentMethodBuilder().build();	@Override	protected Order doBuild() {		Order order = new Order();		order.setOrderDate(orderDate);		order.setShoppingCart(shoppingCart);		order.setPaymentMethod(paymentMethod);		return order;	}	public OrderBuilder orderDate(Date orderDate) {		this.orderDate = orderDate;		return this;	}	public OrderBuilder shoppingCart(ShoppingCart shoppingCart) {		this.shoppingCart = shoppingCart;		return this;	}	public OrderBuilder paymentMethod(PaymentMethod paymentMethod) {		this.paymentMethod = paymentMethod;		return this;	}}