package com.sk.util.builder;import java.util.ArrayList;import java.util.Arrays;import java.util.List;import org.apache.commons.lang.RandomStringUtils;import com.sk.domain.Category;import com.sk.domain.Photo;import com.sk.domain.Product;public class ProductBuilder extends BaseBuilder<Product, ProductBuilder> {	private Category category = new CategoryBuilder().build();	private String title = RandomStringUtils.randomAlphabetic(10);	private String description = RandomStringUtils.randomAlphabetic(500);	private double price = 100d;	private List<Photo> photos = new ArrayList<Photo>();	@Override	protected Product doBuild() {		Product product = new Product();		product.setCategory(category);		product.setTitle(title);		product.setDescription(description);		product.setPrice(price);		product.setPhotos(photos);		return product;	}	public ProductBuilder category(Category category) {		this.category = category;		return this;	}	public ProductBuilder title(String title) {		this.title = title;		return this;	}	public ProductBuilder description(String description) {		this.description = description;		return this;	}	public ProductBuilder price(double price) {		this.price = price;		return this;	}	public ProductBuilder photos(Photo... photos) {		this.photos = Arrays.asList(photos);		return this;	}}