package com.sk.frontend.service;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.sk.frontend.web.interceptor.ShoppingCartInterceptor;import com.sk.frontend.web.util.CookieUtils;import com.sk.service.CacheService;@Servicepublic class ShoppingCartService {		private CacheService cacheService;	@Autowired	public ShoppingCartService(CacheService cacheService) {		this.cacheService = cacheService;	}	public void deleteShoppingCart(HttpServletRequest request, HttpServletResponse response) {		cacheService.delete(ShoppingCartInterceptor.CART);		CookieUtils.deleteCookie(request, response, ShoppingCartInterceptor.CART);	}}