package com.sk.frontend.web.helper;import java.util.Calendar;import java.util.HashMap;import java.util.Map;import java.util.TreeMap;import org.springframework.stereotype.Component;import com.sk.domain.CreditCardType;@Componentpublic class CreditCardPopulatorHelper {	public Map<String, String> getCreditCardTypes() {		Map<String, String> creditCardTypes = new HashMap<String, String>();		for (CreditCardType creditCardType : CreditCardType.values()) {			creditCardTypes.put(creditCardType.name(), creditCardType.getName());		}		return creditCardTypes;	}	public Map<String, String> getMonths() {		Map<String, String> months = new TreeMap<String, String>();		for (int i = 1; i < 13; i++) {			if (i < 10) {				months.put("0" + i, "0" + i);			} else {				months.put("" + i, "" + i);			}		}		return months;	}		public Map<String, String> getYears() {		Map<String, String> years = new TreeMap<String, String>();		int currentYear = Calendar.getInstance().get(Calendar.YEAR);		for (int year = currentYear; year < currentYear + 12; year++) {			years.put("" + year, "" + year);		}		return years;	}	}