package com.sk.frontend.web.controller;import org.junit.Before;import org.junit.Test;import org.junit.runner.RunWith;import org.mockito.Mock;import org.mockito.runners.MockitoJUnitRunner;import org.springframework.mock.web.MockHttpServletRequest;import org.springframework.web.servlet.ModelAndView;import com.sk.service.payment.ResponseStatus;import com.sk.service.payment.VPOSResponse;import com.sk.service.payment.garanti.GarantiVPOSService;import static org.mockito.Mockito.when;import static org.hamcrest.Matchers.equalTo;import static org.hamcrest.MatcherAssert.assertThat;@RunWith(MockitoJUnitRunner.class)public class BonusPaymentControllerTest {	@Mock	private GarantiVPOSService garantiVPOSService;		private MockHttpServletRequest request = new MockHttpServletRequest();		private BonusPaymentController bonusPaymentController;		@Before	public void init(){		bonusPaymentController = new BonusPaymentController(garantiVPOSService);	}		@Test	public void shouldQueryForBonusForCreditCard(){		String cardNumber = "1234567890123456";		when(garantiVPOSService.queryBonus(cardNumber)).thenReturn(new VPOSResponse("10","10",ResponseStatus.SUCCESS));				ModelAndView mav =  bonusPaymentController.query(cardNumber, request);				assertThat(mav.getViewName(), equalTo("bonusQuery"));		assertThat((String)mav.getModel().get("bonus"), equalTo("10"));			}}