package com.sk.frontend.web.helper;import java.util.Calendar;import java.util.Map;import java.util.TreeMap;import org.junit.Before;import org.junit.Test;import com.sk.domain.CreditCardType;import static org.hamcrest.MatcherAssert.assertThat;import static org.hamcrest.Matchers.equalTo;public class CreditCardPopulatorHelperTest {		private CreditCardPopulatorHelper cardPopulatorHelper;	@Before	public void init(){		cardPopulatorHelper = new CreditCardPopulatorHelper();	}	@Test	public void shouldCreateCreditCardTypes() {		Map<String, String> creditCardTypes = cardPopulatorHelper.getCreditCardTypes();		assertThat(creditCardTypes.keySet().contains(CreditCardType.VISA.name()), equalTo(true));		assertThat(creditCardTypes.keySet().contains(CreditCardType.MASTERCARD.name()), equalTo(true));		assertThat(creditCardTypes.keySet().size(), equalTo(2));	}	@Test	public void shouldCreateYears() {		TreeMap<String, String> years = (TreeMap<String, String>) cardPopulatorHelper.getYears();		int currentYear = Calendar.getInstance().get(Calendar.YEAR);		assertThat(years.keySet().size(), equalTo(12));		assertThat(years.firstKey(), equalTo("" + currentYear));		assertThat(years.lastKey(), equalTo("" + (currentYear + 11)));	}	@Test	public void shouldCreateMonths() {		TreeMap<String, String> manths = (TreeMap<String, String>) cardPopulatorHelper.getMonths();		assertThat(manths.keySet().size(), equalTo(12));		assertThat(manths.firstKey(), equalTo("01"));		assertThat(manths.lastKey(), equalTo("12"));	}}